"use strict";

let words = [ "lovely", "amazing", "wonderful", "great", "thoughtful", "generous", "charming", "bold", "unique", "wondrous", "striking", "polished", "elegant", "intellegant", "gracious", "kind", "honest", "entertaining", "witty"];


function random() {
  return words[Math.floor(Math.random() * words.length)]
}

module.exports = {default: words, random};
